import React, { useState, useEffect } from "react";
import { Button, Card } from "react-bootstrap";
import { useNavigate, useLocation } from "react-router-dom";
import Pagination from 'react-bootstrap/Pagination';

import "./ProductCards.css";

const ProductCards = ({ loadedProducts }) => {
    const navigate = useNavigate();
    const location = useLocation();

    const handleViewProduct = (e) => {
        const redirectPath = location.state?.path || `/viewProduct/${e.target.value}`;
        navigate(redirectPath);
    };

    // User is currently on this page
    const [currentPage, setCurrentPage] = useState(1);
    // No of Records to be displayed on each page   
    const recordsPerPage = 10;

    const onPageChange = (page) => {
        setCurrentPage(page);
    };

    const indexOfLastRecord = currentPage * recordsPerPage;
    const indexOfFirstRecord = indexOfLastRecord - recordsPerPage;


    const [currentRecords, setCurrentRecords] = useState();
    const [numPages, setNumPages] = useState();
    const [pageNumbers, setPageNumbers] = useState();

    useEffect(() => {
        console.log(indexOfFirstRecord, indexOfLastRecord, recordsPerPage, numPages, pageNumbers, loadedProducts)
        if (loadedProducts) {
            const tempRecords = loadedProducts.slice(indexOfFirstRecord, indexOfLastRecord);
            setCurrentRecords(tempRecords);
            const tempNumPages = Math.ceil(loadedProducts.length / recordsPerPage);
            setNumPages(tempNumPages);
            setPageNumbers([...Array(numPages + 1).keys()].slice(1));
        }

    }, [indexOfFirstRecord, indexOfLastRecord, recordsPerPage, numPages, loadedProducts]);

    return (
        <div>
            {currentRecords &&
                <>
                    <div className="product-container">
                        {loadedProducts.map((product) => {
                            return <Card className="product-card">
                                <Card.Img variant="top" src={`http://localhost:8080/${product.image}`} className="product-image" />
                                <Card.Body>
                                    <Card.Title>{product.name}</Card.Title>
                                    <Card.Subtitle>${product.price}</Card.Subtitle>
                                </Card.Body>
                                <Button value={product.uid} variant="primary" onClick={handleViewProduct}>View Product</Button>
                            </Card>
                        })}
                    </div>

                    <Pagination size="sm">

                        {pageNumbers.map((pageNum) => {
                            <Pagination.Item key={pageNum} active={pageNum === currentPage}>
                                {pageNum}
                            </Pagination.Item>
                        })

                        }

                    </Pagination>
                </>
            }
            {!loadedProducts &&
                <div className="empty-message">
                    <p>No products are listed at the moment.</p>
                </div>
            }
        </div>
    );
};

export default ProductCards;
