import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { Button, Card } from "react-bootstrap";
import { useHttpClient } from "../hooks/http-hook";

import "./ProductCard.css";

const ProductCard = ({ product }) => {
    const navigate = useNavigate();
    const location = useLocation();

    const [loadedImage, setLoadedImage] = useState();
    const { sendRequest } = useHttpClient();

    const handleViewProduct = (e) => {
        const redirectPath = location.state?.path || `/viewProduct/${e.target.value}`;
        navigate(redirectPath);
    };

    useEffect(() => {
        const fetchImage = async () => {
            try {
                const responseData = await sendRequest(`http://localhost:8080/api/listings/image/${product.image}`);
                setLoadedImage(responseData.image.blob());
            } catch (err) { };
        };
        fetchImage();
    }, [sendRequest]);

    return (
        <Card className="product-card" >
            <Card.Img variant="top" src={URL.createObjectURL(loadedImage)} className="product-image" />
            <Card.Body>
                <Card.Title>{product.name}</Card.Title>
                <Card.Subtitle>${product.price}</Card.Subtitle>
            </Card.Body>
            <Button value={product.uid} variant="primary" onClick={handleViewProduct}>View Product</Button>
        </Card>
    );
};

export default ProductCard;
