                                <Card.Img variant="top" src={`http://localhost:8080/${product.image}`} className="product-image" />
                                <Card.Body>
                                    <Card.Title>{product.name}</Card.Title>
                                    <Card.Subtitle>${product.price}</Card.Subtitle>
